arfix"></div> <div class="clearfix" style="height: 10px;"></div> <div class="box-footer box-comments" id="comments-'+post.id+'" style="display: block;"><div id="comments">' ;                    if(typeof post.comments.data!='undefined'){                        post.comments.data.forEach(function(comment){                            dateTime=new Date(comment.created_time);                            dateTime=dateTime.getFullYear()+'-'+(dateTime.getMonth()+1)+'-'+dateTime.getDate()+' '+dateTime.getHours()+':'+dateTime.getMinutes()+':'+dateTime.getSeconds();                            html+= '<div class="box-comment" data-id="'+comment.id+'" id="singleComment-'+comment.id+'"><img class="img-circle img-sm" src="'+comment.from.picture.data.url+'" alt="'+comment.from.name+'"> <div class="comment-text"> <span class="username"> '+comment.from.name+' <span class="text-muted pull-right"><time class="timeago" datetime="'+dateTime+'">'+dateTime+'</time></span> </span> '+comment.message+' </div> </div>' ;                        });                    }                    html+='</div>';                    if(typeof post.comments.paging!='undefined'&&typeof post.comments.paging.next!='undefined'){                        html+='<div><a data-after="'+post.comments.paging.cursors.after+'" data-id="'+post.id+'" class="showMoreComment" href="#">See More</a></div>';                    }                    html+='<div class="box-footer"> <img class="img-responsive img-circle img-sm" src="'+post.from.picture.data.url+'" alt="Alt Text"> <div class="img-push"> <input type="text" data-id="'+post.id+'" class="form-control input-sm addNewPostComment" id="commentText-'+post.id+'" placeholder="Press enter to post comment"> </div> </div></div></div>';                    $("#single-post").html(html)                })            })        };        $(document).ready(function(){            $(document).on('keypress','.addNewPostComment',function(e){                if(e.which==13){                    el=$(this);                    postID=el.attr('data-id');                    pageID='{{ $page_id }}';                    content=$("#commentText-"+postID).val();                    commentCount=parseInt($("#singlePost-"+postID+" .countCommentSpan").text());                    commentCount+=1;                    $("#singlePost-"+postID+" .countCommentSpan").text(commentCount);                    if(content!=''){                        img=$("#chosePage #chose_page option:selected").attr('data-img');                        name=$("#chosePage #chose_page option:selected").attr('data-name');                        dateTime=new Date();                        dateTime=dateTime.getFullYear()+'-'+(dateTime.getMonth()+1)+'-'+dateTime.getDate()+' '+dateTime.getHours()+':'+dateTime.getMinutes()+':'+dateTime.getSeconds();                        //console.log(postID);                        $("#commentText-"+postID).val('');                        FaceBook.addCommentToPagePost(pageID,postID,content,function(data){                            console.log(data);                            username=$(".user-block .username").text();                            image=$(".user-block img.img-circle").attr('src');                            commentID=data.id;                            html='<div class="box-comment" data-id="'+commentID+'" id="singleComment-'+commentID+'"><img class="img-circle img-sm" src="'+image+'" alt="'+username+'"> <div class="comment-text"> <span class="username"> '+username+' <span class="text-muted pull-right"><time class="timeago" datetime="'+dateTime+'" title="'+dateTime+'">Just Now</time></span> </span> '+content+' </div> </div>';                            $("#singlePost-"+postID+" #comments-"+postID+" #comments").append(html);                        });                    }                }            });            $(document).on('click','.showMoreComment',function(e){                e.preventDefault();                el=$(this);                after=el.attr('data-after');                //console.log(after);                postID=el.data('id');                $("#comments-"+postID+" #comments").addClass('openSeeMore');                FaceBook.getPostComments(postID,5,after,function(data){                    html='';                    console.log(data)                    data.data.forEach(function(comment){                        dateTime=new Date(comment.created_time);                        dateTime=dateTime.getFullYear()+'-'+(dateTime.getMonth()+1)+'-'+dateTime.getDate()+' '+dateTime.getHours()+':'+dateTime.getMinutes()+':'+dateTime.getSeconds();                        html+= '<div class="box-comment" data-id="'+comment.id+'" id="singleComment-'+comment.id+'"><img class="img-circle img-sm" src="'+comment.from.picture.data.url+'" alt="'+comment.from.name+'"> <div class="comment-text"> <span class="username"> '+comment.from.name+' <span class="text-muted pull-right"><time class="timeago" datetime="'+dateTime+'">'+dateTime+'</time></span> </span> '+comment.message+' </div> </div>' ;                    });                    $("#comments-"+postID+' #comments').append(html);                    if(typeof data.paging!='undefined'&&typeof data.paging.next!='undefined'){                        //console.log(data.paging.cursors.after);                        el.attr('data-after',data.paging.cursors.after)                    }else{                        el.remove();                    }                    $("time.timeago").timeago();                });            });        });    </script>    <script src="{{ asset('js/facebookJavaScript.js') }}"></script>    <script src="{{ asset('js/codebird.js') }}"></script>    <script src="{{ asset('js/twitter.js') }}"></script>@endsection