one" class="control-label">{{ Lang::get('home.phone') }}</label>                                    <input type="text" id="phone" name="phone" value="{{ Auth::guard('AppUsers')->user()->phone }}" placeholder="{{ Lang::get('home.enter').Lang::get('home.phone') }}" class="form-control" />                                </div>                                <div class="form-group">                                    <label for="old_password" class="control-label">{{ Lang::get('home.old_password') }}</label>                                    <input type="password" id="old_password" required name="old_password" placeholder="{{ Lang::get('home.enter').Lang::get('home.old_password') }}" class="form-control" />                                </div>                                <div class="form-group">                                    <label for="new_password" class="control-label">{{ Lang::get('home.new_password') }}</label>                                    <input type="password" id="new_password" name="new_password" placeholder="{{ Lang::get('home.enter').Lang::get('home.new_password') }}" class="form-control" />                                </div>                                <div class="form-group">                                    <label for="confirm_password" class="control-label">{{ Lang::get('home.confirm_password') }}</label>                                    <input type="password" id="confirm_password" name="confirm_password" placeholder="{{ Lang::get('home.enter').Lang::get('home.confirm_password') }}" class="form-control" />                                </div>                                <div class="margiv-top-10 text-center">                                    <button type="submit" class="btn green">{{ Lang::get('home.save') }}</button>                                </div>                            </div>                        </div>                    </div>                </div>                <!-- END PROFILE CONTE