<?php if($errors->any()): ?>    <div class="alert alert-danger ">        <button class="close" aria-hidden="true" data-dismiss="alert">&times;</button>        <h4 class="alert-heading"><?php echo e(Lang::get('main.error')); ?></h4>        <ul>            <?php $__currentLoopData = $errors->all(); $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $error): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                <li><?php echo e($error); ?> </li>            <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>        </ul>    </div><?php endif; ?><?php if(!empty( Session::get('success') )): ?>    <div class="alert  alert-success">        <button class="close" aria-hidden="true" data-dismiss="alert">&times;</button>        <h4 class="alert-heading"> <?php echo Session::get('success'); ?></h4>        <?php echo e(Session::forget('success')); ?>    </div><?php endif; ?><?php if(!empty( Session::get('error') )): ?>    <div class="alert  alert-danger">        <button class="close" aria-hidden="true" data-dismiss="alert">&times;</button>        <h4 class="alert-heading"> <?php echo Session::get('error'); ?></h4>        <?php echo e(Session::forget('error')); ?>    </div><?php endif; ?>