-actions">                <button type="submit" class="btn green uppercase">Login</button>                <label class="rememberme check mt-checkbox mt-checkbox-outline">                    <input type="checkbox" name="remember" <?php echo e(old('remember') ? 'checked' : ''); ?> value="1" />Remember                    <span></span>                </label>                <a href="javascript:;" id="forget-password" class="forget-password">Forgot Password?</a>            </div>        </form>        <!-- END LOGIN FORM -->        <!-- BEGIN FORGOT PASSWORD FORM -->        <form class="forget-form" action="index.html" method="post">            <h3 class="font-green">Forget Password ?</h3>            <p> Enter your e-mail address below to reset your password. </p>            <div class="form-group">                <input class="form-control placeholder-no-fix" type="text" autocomplete="off" placeholder="Email" name="email" /> </div>            <div class="form-actions">                <button type="button" id="back-btn" class="btn green btn-outline">Back</button>                <button type="submit" class="btn btn-success uppercase pull-right">Submit</button>            </div>        </form>        <!-- END FORGOT PASSWORD FORM -->    </div><?php $__env->stopSection(); ?>
