cessToken);                        Twitter.initializeCodeBird(accessToken);                        Twitter.getUserDetails(function (reply, rate, err) {                            html='<input type="hidden" id="userID" value="'+reply.id+'">';                            html+='<input type="hidden" id="accessToken" value="'+accessToken.oauth_token+'">';                            html+='<input type="hidden" id="expiresIn" value="">';                            html+='<input type="hidden" id="accessTokenSecret" value="'+accessToken.oauth_token_secret+'">';                            html+='<div class="col-lg-12 '+(($.inArray(reply.id,allIDS)===-1)?'':'hasAddPage')+'">'+(($.inArray(reply.id,allIDS)===-1)?'':'<span style="color:red;margin-top: 10px;" class="pull-right">Profile already managed in this account.</span>')+'<div class="checkbox"><label><input '+(($.inArray(reply.id,allIDS)===-1)?'':'disabled')+' type="checkbox" class="socialFacebookCheckbox " data-type="twitter" data-image="'+reply.profile_image_url+'" data-oauth-token="'+accessToken.oauth_token+'" data-oauth-token-secret="'+accessToken.oauth_token_secret+'" data-name="'+reply.name+'" value="'+reply.id+'" id=""><img src="'+reply.profile_image_url+'" alt="'+reply.name+'"><span>'+reply.name+'</span></label></div></div>';                            html+='<div class="clearfix"></div>';                            html+='<div class="col-lg-12"><button id="socialFacebookAddProfile" disabled class="btn btn-primary pull-right">Add profiles</button></div>';                            $("#socialLogin").addClass('hidden');                            $("#socialLoginBox").html(html);                            console.log(reply)                            console.log(rate)                            console.log(err)                        });                    }                });            } else {                alert('please set request token before call it');            }        });    });<